def create_hidden_folder():
    appdata_path = os.getenv('APPDATA')
    hidden_folder = os.path.join(appdata_path, '.my_hidden_folder')
    if not os.path.exists(hidden_folder):
        os.mkdir(hidden_folder)
    return hidden_folder

