def hide_folder(folder_path):
    if os.name == 'nt':
        os.system(f'attrib +h "{folder_path}"')

# Ø¯Ø§Ù„Ø© Ù„Ù„ØªØ­Ù‚Ù‚ Ù…Ù† ØµÙ„Ø§Ø­ÙŠØ© Ø§Ù„Ù…ÙØªØ§Ø­ Ø¨Ø§Ø³ØªØ®Ø¯Ø§Ù… Firebase
def check_key_validity(key, first_activation=False):
    firebase_url = 'https://activation-key-899-default-rtdb.asia-southeast1.firebasedatabase.app/activation_keys.json'
    response = requests.get(firebase_url)

    if response.status_code == 200:
        data = response.json()
        if key not in data:
            #print(f"âŒ The code {key} is incorrect.")
            print(Fore.RED + f"âŒ The code is incorrect.")
            return False
        else:
            expiry_date_str = data[key]['data']
            expiry_date = datetime.strptime(expiry_date_str, '%d/%m/%Y')
            current_date = datetime.now()

            devices_count = int(data[key].get('devices', 0))  # Ù†Ù‚Ø±Ø£ Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ø¬Ù‡Ø²Ø©

            # Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ø£ÙˆÙ„ Ù…Ø±Ø© ØªÙØ¹ÙŠÙ„ Ø£Ùˆ Ø§Ù„Ù…ÙØªØ§Ø­ Ù…Ù†ØªÙ‡ÙŠ
            if first_activation:
                if devices_count <= 0:
                    print(Fore.RED +"âŒ The key is used up or no devices are available for this key.")
                    return False
                else:
                    # ØªÙ‚Ù„ÙŠÙ„ Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ø¬Ù‡Ø²Ø© Ø¨Ù…Ù‚Ø¯Ø§Ø± 1
                    updated_devices_count = devices_count - 1
                    update_devices_count_in_firebase(key, updated_devices_count)

            if current_date > expiry_date:
                print(Fore.RED +f"âŒ The code expired on {expiry_date_str}.")
                #print(Fore.RED +f"âŒ The code {key} expired on {expiry_date_str}.")
                return False
            else:
                print(Fore.GREEN +f"âœ… The code is valid until {expiry_date_str}.")
                #print(f"âœ… The code {key} is valid until {expiry_date_str}.")

                return True
    else:
        print(Fore.RED + f"âŒ Failed to read data from : {response.status_code}")
        return False

# Ø¯Ø§Ù„Ø© Ù„ØªØ­Ø¯ÙŠØ« Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ø¬Ù‡Ø²Ø© Ø¹Ù„Ù‰ Firebase
def update_devices_count_in_firebase(key, new_count):
    firebase_update_url = f"https://activation-key-899-default-rtdb.asia-southeast1.firebasedatabase.app/activation_keys/{key}/devices.json"
    response = requests.put(firebase_update_url, json=new_count)
    if response.status_code == 200:
        print(Fore.YELLOW +f"ðŸ“‰ The number of devices has been updated to {new_count}.")
        #print(f"ðŸ“‰ ØªÙ… ØªØ­Ø¯ÙŠØ« Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ø¬Ù‡Ø²Ø© Ø¥Ù„Ù‰ {new_count}.")
    else:
        print(Fore.RED +f"âŒ Failed to update the number of devices: {response.status_code}")

